package com.samourai.whirlpool.server.services.rpc;

import static org.springframework.boot.test.context.SpringBootTest.WebEnvironment.RANDOM_PORT;

import com.samourai.whirlpool.server.integration.AbstractJsonRpcClientTest;
import java.util.HashMap;
import java.util.Map;
import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.Disabled;
import org.junit.jupiter.api.Test;
import org.springframework.boot.test.context.SpringBootTest;

@SpringBootTest(webEnvironment = RANDOM_PORT)
@Disabled
public class RpcClientServiceTest extends AbstractJsonRpcClientTest {

  @Test
  public void getRawTransaction() {
    Map<String, String> expectedHexs = new HashMap<>();
    expectedHexs.put(
        "cb2fad88ae75fdabb2bcc131b2f4f0ff2c82af22b6dd804dc341900195fb6187",
        "");
    expectedHexs.put(
        "7ea75da574ebabf8d17979615b059ab53aae3011926426204e730d164a0d0f16",
        "");

    Map<String, Long> expectedTimes = new HashMap<>();
    expectedTimes.put(
        "cb2fad88ae75fdabb2bcc131b2f4f0ff2c82af22b6dd804dc341900195fb6187", 1523482219000L);
    expectedTimes.put(
        "7ea75da574ebabf8d17979615b059ab53aae3011926426204e730d164a0d0f16", 1526036025000L);

    for (Map.Entry<String, String> entry : expectedHexs.entrySet()) {
      String txid = entry.getKey();
      String txhex = entry.getValue();

      // TEST
      RpcRawTransactionResponse rawTxResponse = rpcClientService.getRawTransaction(txid).get();

      // VERIFY
      Assertions.assertEquals(txhex, rawTxResponse.getHex());
      Assertions.assertTrue(rawTxResponse.getConfirmations() > 80);

      long expectedTime = expectedTimes.get(txid);
      Assertions.assertEquals(expectedTime, rawTxResponse.getTxTime());
    }
  }
}
